import React from "react";

import Chart from "../Chart/Chart";

const ExpensesChart = props => {
    const chartDataPoints = [
        { label: "Jan", value: 0 },
        { label: "Feb", value: 0 },
        { label: "Mar", value: 0 },
        { label: "Apr", value: 0 },
        { label: "May", value: 0 },
        { label: "Jun", value: 0 },
        { label: "Jul", value: 0 },
        { label: "Aug", value: 0 },
        { label: "Sep", value: 0 },
        { label: "Oct", value: 0 },
        { label: "Nov", value: 0 },
        { label: "Dec", value: 0 }
    ];
// Loop through the expenses and get the month. 
for (const expense in props.expenses) {
    const expenseMonth = expense.date.getMonth();

    // expenseMonth will match the corresponding index.
    chartDataPoints[expenseMonth].value += expense.amount;
}

return <Chart dataPoints={chartDataPoints} />;

};
