import React, { useState } from 'react';

import './ExpensesFilter.css';

const ExpensesFilter = (props) => {
  const [changedYear, setChangedYear] = useState("") 


  const yearChangeHandler = (event) => {

    // setting changedYear(the value React is holding) as the event value (user's input.)
    setChangedYear(event.target.value);  // get the new year from the selector
    console.log(event.target.value, "lol")
    console.log(changedYear, "lol!")

  }

  const sendHandler = (event) => {
    event.preventDefault();

    // Then, send it through props to the parent(expenses.js) by calling parent's function. 
    // props.parentFunctionPointer(childData)
    props.onYearFiltered(changedYear);
  }


  return (
    <div className='expenses-filter'>
      <div className='expenses-filter__control'>
        <label>Filter by year</label>
        <select onChange={yearChangeHandler}>
          <option value='2022'>2022</option>
          <option value='2021'>2021</option>
          <option value='2020'>2020</option>
          <option value='2019'>2019</option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
